mapper.insert:
INSERT INTO warn_rule (
    rule_code, rule_name, battery_type_id, 
    min_threshold, max_threshold, warn_level, is_deleted
) VALUES (
    #{ruleCode}, #{ruleName}, #{batteryTypeId}, 
    #{minThreshold}, #{maxThreshold}, #{warnLevel}, #{isDeleted}
)

mapper.select:
SELECT * FROM warn_rule 
WHERE rule_id = #{ruleId} 
AND is_deleted = false

SELECT * FROM warn_rule 
WHERE battery_type_id = #{batteryTypeId} 
AND is_deleted = false
